import React, { useState } from 'react';
import './LoginSign.css'; // Assuming your CSS file is named LoginSign.css
import usericon from '../assests/person.png';
import emailicon from '../assests/email.png';
import passwordicon from '../assests/password.png';
import icon from '../assests/icon.jpeg';

function LoginSignup() {
  const [action, setAction] = useState('Sign Up');

  const [isRelocated, setRelocated] = useState(false);

  const handleRelocate = () => {
    setRelocated((prev) => !prev);
  };

  return (
    <div className={`box ${isRelocated ? 'relocated' : ''}`}>
      <div className='icon-container'>
        <img src={icon} alt=''></img>
      </div>

      <div className='container'>
        <div className='header'>
          <div className='text'>{action}</div>
          <div className='underline'></div>
        </div>

        <div className='inputs'>
          {action === 'Login' ? (
            <div></div>
          ) : (
            <div className='input'>
              <img src={usericon} alt='' />
              <input type='text' placeholder='Name'></input>
            </div>
          )}

          <div className='input'>
            <img src={emailicon} alt='' />
            <input type='email' placeholder='Email'></input>
          </div>

          <div className='input'>
            <img src={passwordicon} alt='' />
            <input type='password' placeholder='Password'></input>
          </div>
        </div>

        {action === 'Sign Up' ? (
          <div></div>
        ) : (
          <div className='forgot-password'>
            Forgot Password?<span>Click Here!</span>
          </div>
        )}

        <div className='submit-container'>
          <div
            className={action === 'Login' ? 'submit gray' : 'submit'}
            onClick={() => {
              setAction('Sign Up');
            }}
          >
            Sign Up
          </div>
          <div
            className={action === 'Sign Up' ? 'submit gray' : 'submit'}
            onClick={() => {
              setAction('Login');
            }}
          >
            Login
          </div>
        </div>
      </div>
    </div>
  );
}

export default LoginSignup;
